import React from 'react';
import './NewExpense.css';
import ExpenseForm from './ExpenseForm';

const NewExpense = (props) => {
    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id:Math.random().toString()
        };
        props.onAddExpense(expenseData);
    };
    let customStyle1 = "visibility: hidden";
    let customStyle2 = "visibility: visible";
    const popupHandler = () => {
        customStyle1 = "visibility: visible";
        customStyle2 = "visibility: hidden";
    };


    return (


        <div className='new-expense'>
            <button style={customStyle2} onClick={popupHandler}>Add New Expense</button>
            <ExpenseForm style={customStyle1} onSaveExpenseData={saveExpenseDataHandler}/>

        </div>
    );
};

export default NewExpense;
